jQuery(document).ready(function($){
    $('.tabs__caption').on('click', 'li:not(.active)', function(){
        $(this).siblings().removeClass('active');
        $(this).addClass('active').closest('div.tabs').find('div.tabs__content').removeClass('active').eq($(this).index()).addClass('active');
    });
    $('.slider--experts .owl-carousel').owlCarousel({
		dots: true,
		nav: true,
		loop: true,
		stagePadding: 0,
		smartSpeed: 450,
        mouseDrag: true,
        responsive : {
            0 : {  
		        items: 1,
            },
            500 : {
                items: 2,
            },
            768 : {
                items: 3,
            }
        }
    });
    $('.slider--testimonials .owl-carousel').owlCarousel({
		items: 1,
		dots: true,
		nav: true,
		center: true,
		loop: true,
		stagePadding: 0,
		smartSpeed: 450,
		mouseDrag: true
    });
    $('.slider--reasons .owl-carousel').owlCarousel({
		items: 1,
		dots: true,
		nav: true,
		center: true,
		loop: true,
		stagePadding: 0,
		smartSpeed: 450,
		mouseDrag: true
    });
    /*Select*/
    $('select').each(function(){
        var $this = $(this), numberOfOptions = $(this).children('option').length;
  
        $this.addClass('select-hidden');
        $this.wrap('<div class="select"></div>');
        $this.after('<div class="select-styled"></div>');
  
        var $styledSelect = $this.next('div.select-styled');
        $styledSelect.text($this.children('option').eq(0).text());
  
        var $list = $('<ul />', {
            'class': 'select-options'
        }).insertAfter($styledSelect);
  
        for (var i = 0; i < numberOfOptions; i++) {
            $('<li />', {
                text: $this.children('option').eq(i).text(),
                rel: $this.children('option').eq(i).val()
            }).appendTo($list);
        }
  
        var $listItems = $list.children('li');
  
        $styledSelect.click(function(e) {
            e.stopPropagation();
            $('div.select-styled.active').not(this).each(function(){
                $(this).removeClass('active').next('ul.select-options').hide();
            });
            $(this).toggleClass('active').next('ul.select-options').toggle();
        });
  
        $listItems.click(function(e) {
            e.stopPropagation();
            $styledSelect.text($(this).text()).removeClass('active');
            $this.val($(this).attr('rel')).attr('data-select-id',$(this).attr('rel'));
            $list.hide();
        });
  
        $(document).click(function() {
            $styledSelect.removeClass('active');
            $list.hide();
        });
    });

    // Accordion
    var acc = document.getElementsByClassName("button--accordion");
    var i;

    for (i = 0; i < acc.length; i++) {
        acc[i].addEventListener("click", function() {
            /* Toggle between adding and removing the "active" class,
            to highlight the button that controls the panel */
            this.classList.toggle("active");

            /* Toggle between hiding and showing the active panel */
            var panel = this.nextElementSibling;
            if (panel.style.display === "block") {
                panel.style.display = "none";
            } else {
                panel.style.display = "block";
            }
        });
    }
});